package com.bilgeadam.boost.java.ödev;

public class Odev002_FahreneitToCelcius {
	
	public static void main(String[] args) {
		
		// TODO Auto-generated method stub
		
		float f;
		f = 132f;
		float c = (f - 32) * (5f / 9f);
		
		System.out.println("Girilen Fahreneit değerinin Celcius da ki karşılığı: " + c);
	}
	
}
